import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ScheduleTable from './components/ScheduleTable';
import './index.css';

function App() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [timeWindow, setTimeWindow] = useState('');
  const [taskCategory, setTaskCategory] = useState('');
  const [prompt, setPrompt] = useState('');
  const [priority, setPriority] = useState('');
  const [schedules, setSchedules] = useState([]);

  useEffect(() => {
    axios.get('/api/schedules')
      .then(response => setSchedules(response.data))
      .catch(error => console.error('Error fetching schedules:', error));
  }, []);

  const handleAddSchedule = () => {
    axios.post('/api/schedules', { title, description, date, timeWindow, taskCategory, prompt, priority })
      .then(response => {
        setSchedules([...schedules, response.data]);
        setTitle('');
        setDescription('');
        setDate('');
        setTimeWindow('');
        setTaskCategory('');
        setPrompt('');
        setPriority('');
      })
      .catch(error => console.error('Error adding schedule:', error));
  };

  return (
    <div className="App">
      <h1>Scheduler</h1>
      <form onSubmit={(e) => { e.preventDefault(); handleAddSchedule(); }}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Time Window (e.g., 12pm - 3pm)"
          value={timeWindow}
          onChange={e => setTimeWindow(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Task Category"
          value={taskCategory}
          onChange={e => setTaskCategory(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Prompt"
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Priority"
          value={priority}
          onChange={e => setPriority(e.target.value)}
          required
        />
        <button type="submit">Add Schedule</button>
      </form>
      <ScheduleTable schedules={schedules} />
    </div>
  );
}

export default App;
